import { Modal, Form, Input, Button, Select, Typography } from 'antd';
import { useForm } from 'react-hook-form';
import FormItem from "antd/es/form/FormItem";

const { Text } = Typography

type Props = {
    visible: boolean;
    onCancel: () => void;
};

type FormData = {
    comment: string;
    selection: string[];
};

const options = [
    { label: 'Option 1', value: 'option1' },
    { label: 'Option 2', value: 'option2' },
    { label: 'Option 3', value: 'option3' },
    { label: 'Option 4', value: 'option4' },
];

const SecondComponent = ({ visible, onCancel }: Props) => {
    const { handleSubmit, register } = useForm<FormData>();

    const onSubmit = (data: FormData) => {
        console.log(data);
        onCancel();
    };

    return (
        <Modal visible={visible} onCancel={onCancel} footer={[
            <Form.Item>
                <Button type="primary" htmlType="submit">
                    Sign Up
                </Button>
                <Button onClick={onCancel}>Back</Button>
            </Form.Item>
        ]}>
            <FormItem>
            <form onSubmit={handleSubmit(onSubmit)}>
                <Text>Пожалуйста, выберите нужные вам услуги из предлагаемого списка. Если у вас есть какие-то дополнительные требования или пожелания, пожалуйста, опишите их в комментариях. </Text>
                <Form.Item label="Selection" name="selection">
                    <Select
                        mode="multiple"
                        showSearch
                        options={options}
                        filterOption={(input: string, option: any) =>
                            option?.label.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                        optionFilterProp="label"
                    />
                </Form.Item>
                <Form.Item label="Comment" name="comment">
                    <Input.TextArea rows={4} />
                </Form.Item>
            </form>
            </FormItem>
        </Modal>
    );
};

export default SecondComponent;
