import React from 'react';
import { Modal, Form, Input, Button } from 'antd';
import { useForm } from 'react-hook-form';

type Props = {
    onClose: () => void;
};

const mockTags = ['react', 'typescript', 'ant design', 'modal'];

const ModalComponent = ({ onClose }: Props) => {
    const { register, handleSubmit } = useForm();

    const onSubmit = (data: any) => {
        console.log(data);
        onClose();
    };

    return (
        <>
            <Form onFinish={handleSubmit(onSubmit)}>
                <Form.Item label="Search by tags">
                    <Input type="text" {...register('tags')} />
                </Form.Item>
                <Form.Item label="Comment">
                    <Input type="textarea" {...register('comment')} />
                </Form.Item>
                <Form.Item>
                    <Button type="primary" htmlType="submit">
                        Sign Up
                    </Button>
                    <Button onClick={onClose}>Back</Button>
                </Form.Item>
            </Form>
            <Button onClick={onClose} style={{ position: 'absolute', top: 0, right: 0 }}>
                X
            </Button>
        </>
    );
};

export default ModalComponent;
