import React, {useState} from 'react';
import { Modal, Form, Input, Button } from 'antd';
import { useForm } from 'react-hook-form';

type Props = {
    onClose: () => void;
};

type Tag = {
    id: number;
    name: string;
};

const mockData : Tag[] = [
    { id: 1, name: 'React' },
    { id: 2, name: 'TypeScript' },
    { id: 3, name: 'Ant Design' },
    { id: 4, name: 'Modal' },
];

const SecondComponent = ({ onClose }: Props) => {
    const { register, handleSubmit } = useForm();
    const [filteredData, setFilteredData] = useState(mockData);

    const onSubmit = (data: any) => {
        console.log(data);
        onClose();
    };

    const handleSearch = (value: string) => {
        if (!value) {
            setFilteredData(mockData);
        } else {
            const filtered = mockData.filter((tag) =>
                tag.name.toLowerCase().includes(value.toLowerCase())
            );
            setFilteredData(filtered);
        }
    };

    return (
        <>
            <Form onFinish={handleSubmit(onSubmit)}>
                <Form.Item>
                    <h3 style={{ textAlign: 'center' }}>Modal Title</h3>
                </Form.Item>
                <Form.Item>
                    <Input.Search
                        placeholder="Search by tags"
                        onSearch={handleSearch}
                        style={{ width: '80%', marginRight: 'auto' }}
                    />
                </Form.Item>
                <Form.Item label="Comment">
                    <Input type="textarea" {...register('comment')} />
                </Form.Item>
                <Form.Item>
                    <Button type="primary" htmlType="submit">
                        Sign Up
                    </Button>
                    <Button onClick={onClose}>Back</Button>
                </Form.Item>
            </Form>
            <Button onClick={onClose} style={{ position: 'absolute', top: 0, right: 0 }}>
                X
            </Button>
        </>
    );
};

export default SecondComponent;
